campo=[]

try:
    while (True):
        campo.append(list(map(int, input().split())))
except (EOFError):
    pass

res=contandoMinas(campo)

for i in range(len(res)):
    cadena=str(res[i][0])
    for j in range(1,len(res[0])):
        cadena=cadena+" "+str(res[i][j])
    print(cadena)
